import React, { Component } from 'react';

export default class MyError extends Component {

    constructor(props) {
        super(props)
        this.state = { 
            errorJustHappened: false,
            errorCount: 0
        }
    }

    render() {
        const { errorCount } = this.props
        if (errorCount > 0) {
            new Error('Uncaught')
        }
        return (
            <div>
                <button onClick={this.handleClick} >
                    Error
                </button>
            </div>
        )
    }

    handleClick = () => {
        const { errorCount } = this.props
        this.setState({ errorCount: 1 })
    }
}
