import React, { Component } from 'react';

export default class MyError extends Component {

    constructor(props) {
        super(props)
        this.state = { errorJustHappened: false }
    }

    render() {
        return (
            <div>
                <button onClick={this.handleClick} >
                    Error
                </button>
            </div>
        )
    }

    handleClick = () => {
        throw new Error('Uncaught');
    }
}
